import React, { useState, useEffect, useRef } from "react";
import { Typography, Button } from "@mui/material";
import { useParams } from "react-router-dom";
import fetchModel from "../../lib/fetchModelData";
import CommentCustom from "../CustomComment";
import "./styles.css";

function UserPhotos(props) {
  const user = props.user;
  const { userId } = useParams();
  const [photos, setPhotos] = useState([]); // State to store the photo list
  const uploadInputRef = useRef(null);
  const fetchUserPhotos = async () => {
    try {
      const data = await fetchModel(`photo/${userId}`);
      setPhotos(data); // Assuming data is an array of photos
    } catch (error) {
      console.error("Error fetching user photos:", error);
    }
  };

  useEffect(() => {
    fetchUserPhotos();
  }, [userId]);

  const addComment = () => {
    fetchUserPhotos(); // Refetch photos after comment addition
  };

  const handleAddPhoto = () => {
    // Implement the functionality to add a photo
    // This can be a modal or a redirect to a photo upload page
    console.log("Add photo button clicked");
  };

  if (photos.length === 0) {
    return <div>No photos found for this user.</div>;
  }

  return (
    <div className="user-photos-container">
      <Typography variant="body1">User Photos for User ID: {userId}</Typography>
      {props.user._id === userId && (
        <div>
          <input type="file" accept="image/*" ref={uploadInputRef} />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddPhoto}
            style={{ marginBottom: "20px" }}
          >
            Add Photo
          </Button>
        </div>
      )}
      {photos.map((photo) => (
        <div key={photo._id} className="photo-container">
          <img
            src={`http://localhost:8081/api/photo/images/${photo.file_name}`}
            alt={`Photo by user ${userId}`}
          />
          <div className="photo-details">
            <Typography variant="body2">Time: {photo.date_time}</Typography>
            <Typography variant="h6">Comments</Typography>
            <div className="comments-section">
              {photo.comments.map((comment) => (
                <div key={comment._id} className="comment-item">
                  <Typography variant="body2">
                    Comment: {comment.comment}
                  </Typography>
                  <Typography variant="body2">
                    Time: {comment.date_time}
                  </Typography>
                </div>
              ))}
            </div>
            <CommentCustom
              photo_id={photo._id}
              user={user}
              addComment={addComment}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default UserPhotos;
